import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import {
  getAuth,
  GoogleAuthProvider,
  signInWithPopup,
  Auth,
  onAuthStateChanged,
  User
} from 'firebase/auth';
import { initializeApp, FirebaseApp } from 'firebase/app';
import { environment } from '../../enviroment';

@Component({
  selector: 'app-authentication',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './authentication.component.html',
  styleUrls: ['./authentication.component.scss']
})
export class AuthenticationComponent implements OnInit {
  private firebaseApp: FirebaseApp;
  private auth: Auth;
  user: User | null = null;
  message: string = '';

  constructor() {
    try {
      this.validateFirebaseConfig();
      this.firebaseApp = initializeApp(environment.firebaseConfig);
      this.auth = getAuth(this.firebaseApp);
    } catch (error) {
      console.error('Firebase Initialization Error:', error);
      this.message = 'Firebase configuration error. Please check your setup.';
    }
  }

  private validateFirebaseConfig() {
    const config = environment.firebaseConfig;
    const requiredFields = [
      'apiKey',
      'authDomain',
      'projectId',
      'storageBucket',
      'messagingSenderId',
      'appId',
      'measurementId'
    ];

    for (const field of requiredFields) {
      if (!config[field]) {
        throw new Error(`Missing Firebase configuration: ${field}`);
      }
    }
  }

  ngOnInit() {
    try {
      onAuthStateChanged(this.auth, (user) => {
        this.user = user;
        if (user) {
          this.message = `Welcome, ${user.displayName || 'User'}!`;
        }
      });
    } catch (error) {
      console.error('Auth State Change Error:', error);
      this.message = 'Authentication initialization failed.';
    }
  }

  async googleSignIn() {
    try {
      const provider = new GoogleAuthProvider();
      provider.addScope('profile');
      provider.addScope('email');

      const result = await signInWithPopup(this.auth, provider);
      if (result.user) {
        this.user = result.user;
        this.message = `Welcome, ${this.user.displayName || 'User'}!`;
      } else {
        throw new Error('No user returned from sign-in');
      }
    } catch (error: any) {
      console.error('Google Sign-In Error:', error);
      this.handleAuthError(error);
    }
  }

  async signOut() {
    try {
      await this.auth.signOut();
      this.user = null;
      this.message = 'Signed out successfully.';
    } catch (error: any) {
      console.error('Sign-Out Error:', error);
      this.message = `Sign-Out Failed: ${error.message}`;
    }
  }

  private handleAuthError(error: any) {
    switch (error.code) {
      case 'auth/popup-blocked':
        this.message = 'Authentication popup was blocked. Please enable popups.';
        break;
      case 'auth/popup-closed-by-user':
        this.message = 'Sign-in popup was closed before completion.';
        break;
      case 'auth/internal-error':
        this.message = 'Internal authentication error. Check configuration.';
        break;
      default:
        this.message = `Sign-In Failed: ${error.message}`;
    }
  }
}
