import { Component, inject } from '@angular/core';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Auth, createUserWithEmailAndPassword, signInWithEmailAndPassword, getAuth } from 'firebase/auth';
import { initializeApp } from 'firebase/app';
import { environment } from '../../environment';

// Initialize Firebase
const firebaseApp = initializeApp(environment.firebaseConfig);

@Component({
  selector: 'app-authentication',
  standalone: true,
  imports: [ReactiveFormsModule], // Use Angular's standalone imports
  templateUrl: './authentication.component.html',
  styleUrls: ['./authentication.component.scss'],
})
export class AuthenticationComponent {
  private auth: Auth = getAuth(firebaseApp); // Initialize Firebase Auth
  private fb = inject(FormBuilder); // Modern Angular DI

  authForm: FormGroup; // Reactive Form for authentication
  isSignUpMode: boolean = false; // Toggle between Sign-In and Sign-Up
  message: string = ''; // Feedback message

  constructor() {
    this.authForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]], // Email field
      password: ['', [Validators.required, Validators.minLength(6)]], // Password field
      confirmPassword: [''], // Confirm Password field (for sign-up)
    });
  }

  async signUp() {
    if (this.authForm.invalid || this.authForm.value.password !== this.authForm.value.confirmPassword) {
      this.message = 'Please fill out the form correctly.';
      return;
    }

    try {
      const { email, password } = this.authForm.value;
      const userCredential = await createUserWithEmailAndPassword(this.auth, email, password);
      this.message = `Welcome, ${userCredential.user.email}! Your account has been created.`;
    } catch (error: any) {
      this.message = `Error: ${error.message}`;
    }
  }

  async signIn() {
    if (this.authForm.invalid) {
      this.message = 'Please fill out the form correctly.';
      return;
    }

    try {
      const { email, password } = this.authForm.value;
      const userCredential = await signInWithEmailAndPassword(this.auth, email, password);
      this.message = `Welcome back, ${userCredential.user.email}!`;
    } catch (error: any) {
      this.message = `Error: ${error.message}`;
    }
  }
}
