import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { getAuth, onAuthStateChanged, signInWithEmailAndPassword, createUserWithEmailAndPassword, signOut } from 'firebase/auth';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, set, onValue } from 'firebase/database';
import { environment } from '../../enviroment';

@Component({
  selector: 'app-authentication',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './authentication.component.html',
  styleUrls: ['./authentication.component.scss'],
})
export class AuthenticationComponent implements OnInit {
  private auth = getAuth(initializeApp(environment.firebaseConfig));
  email: string = '';
  password: string = '';
  message: string = '';
  user: any = null;
  users: any[] = [];
  chatMessage: string = '';
  messages: any[] = [];
  selectedUser: any = null;

  ngOnInit() {
    this.monitorAuthState();
    this.fetchUsers();
  }

  async createAccount() {
    try {
      const userCredential = await createUserWithEmailAndPassword(this.auth, this.email, this.password);
      this.user = userCredential.user;

      // Save user data to Firebase Realtime Database
      const db = getDatabase();
      await set(ref(db, `users/${this.user.uid}`), {
        email: this.user.email,
        uid: this.user.uid,
      });

      this.message = `Account created for ${this.user.email}!`;
    } catch (error: any) {
      console.error('Sign-Up Error:', error.message);
      this.message = `Sign-Up Error: ${error.message}`;
    }
  }

  async loginEmailPassword() {
    try {
      const userCredential = await signInWithEmailAndPassword(this.auth, this.email, this.password);
      this.user = userCredential.user;
      this.message = `Welcome, ${this.user.email}!`;
    } catch (error: any) {
      console.error('Login Error:', error.message);
      this.message = `Login Error: ${error.message}`;
    }
  }

  async logout() {
    try {
      await signOut(this.auth);
      this.user = null;
      this.message = 'Signed out successfully.';
    } catch (error: any) {
      console.error('Sign-Out Error:', error.message);
      this.message = `Sign-Out Error: ${error.message}`;
    }
  }

  monitorAuthState() {
    onAuthStateChanged(this.auth, (user) => {
      if (user) {
        this.user = user;
        this.message = `Welcome, ${user.email || 'User'}!`;
      } else {
        this.user = null;
        this.message = 'You are not logged in.';
      }
    });
  }

  fetchUsers() {
    const db = getDatabase();
    const usersRef = ref(db, 'users');

    onValue(usersRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        this.users = Object.values(data).filter((user: any) => user.uid !== this.user?.uid);
      }
    });
  }

  selectUser(userId: string) {
    this.selectedUser = this.users.find((user) => user.uid === userId);
    this.fetchMessages(userId);
  }

  sendMessage(receiverId: string, message: string) {
    if (!message.trim()) return;

    const db = getDatabase();
    const chatRef = ref(db, `chats/${this.user.uid}_${receiverId}`);

    set(chatRef, {
      sender: this.user.uid,
      receiver: receiverId,
      message,
      timestamp: Date.now(),
    });

    this.chatMessage = ''; // Clear the message input
  }

  fetchMessages(receiverId: string) {
    const db = getDatabase();
    const chatRef = ref(db, `chats/${this.user.uid}_${receiverId}`);

    onValue(chatRef, (snapshot) => {
      const data = snapshot.val();
      this.messages = data ? Object.values(data) : [];
    });
  }
}
<div class="auth-container">
  <h1>Firebase Authentication</h1>

  <div *ngIf="!user; else loggedIn">
    <div>
      <label for="email">Email:</label>
      <input id="email" [(ngModel)]="email" type="email" placeholder="Enter your email" />
    </div>
    <div>
      <label for="password">Password:</label>
      <input id="password" [(ngModel)]="password" type="password" placeholder="Enter your password" />
    </div>
    <div>
      <button (click)="loginEmailPassword()" class="btn">Log in</button>
      <button (click)="createAccount()" class="btn">Sign up</button>
    </div>
    <p *ngIf="message" class="message">{{ message }}</p>
  </div>

  <ng-template #loggedIn>
    <h2>Welcome, {{ user.email }}</h2>
    <button (click)="logout()" class="btn">Log out</button>

    <div>
      <h3>All Users</h3>
      <ul>
        <li *ngFor="let user of users" (click)="selectUser(user.uid)">
          {{ user.email }}
        </li>
      </ul>
    </div>

    <div *ngIf="selectedUser">
      <h3>Chat with {{ selectedUser.email }}</h3>
      <div class="messages">
        <p *ngFor="let msg of messages">
          {{ msg.sender === user.uid ? 'Me' : selectedUser.email }}: {{ msg.message }}
        </p>
      </div>
      <input [(ngModel)]="chatMessage" placeholder="Type your message..." />
      <button (click)="sendMessage(selectedUser.uid, chatMessage)">Send</button>
    </div>
  </ng-template>
</div>
