import { Component } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { Auth, getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from 'firebase/auth';
import { initializeApp } from 'firebase/app';
import { environment } from '../../enviroment';

// Initialize Firebase
const firebaseApp = initializeApp(environment.firebaseConfig);

@Component({
  selector: 'app-authentication',
  standalone: true,
  imports: [FormsModule], // Import FormsModule here
  templateUrl: './authentication.component.html',
  styleUrls: ['./authentication.component.scss'],
})
export class AuthenticationComponent {
  private auth: Auth = getAuth(firebaseApp);
  email: string = '';
  password: string = '';
  isSignUpMode: boolean = false;
  message: string = '';

  async signUp() {
    try {
      const userCredential = await createUserWithEmailAndPassword(this.auth, this.email, this.password);
      this.message = `Welcome, ${userCredential.user.email}! Your account has been created.`;
    } catch (error: any) {
      this.message = `Error: ${error.message}`;
    }
  }

  async signIn() {
    try {
      const userCredential = await signInWithEmailAndPassword(this.auth, this.email, this.password);
      this.message = `Welcome back, ${userCredential.user.email}!`;
    } catch (error: any) {
      this.message = `Error: ${error.message}`;
    }
  }
}
