import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { 
  getAuth, 
  GoogleAuthProvider, 
  signInWithPopup, 
  Auth, 
  onAuthStateChanged 
} from 'firebase/auth';
import { initializeApp } from 'firebase/app';
import { environment } from '../../enviroment'; // Correct import path

@Component({
  selector: 'app-authentication',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './authentication.component.html',
  styleUrls: ['./authentication.component.scss']
})
export class AuthenticationComponent implements OnInit {
  private firebaseApp = initializeApp(environment.firebaseConfig);
  private auth: Auth = getAuth(this.firebaseApp);

  user: any = null;
  items: { id: number; name: string }[] = [];
  message: string = '';

  constructor() {}

  ngOnInit() {
    // Listen for authentication state changes
    onAuthStateChanged(this.auth, (user) => {
      if (user) {
        this.user = user;
        this.message = `Welcome, ${user.displayName || 'User'}!`;
      } else {
        this.user = null;
      }
    });
  }

  async googleSignIn() {
    try {
      const provider = new GoogleAuthProvider();

      // Add these scopes if needed
      provider.addScope('profile');
      provider.addScope('email');

      const result = await signInWithPopup(this.auth, provider);

      // Verify the user object
      if (result.user) {
        this.user = result.user;
        this.message = `Welcome, ${this.user.displayName || 'User'}! You are signed in with Google.`;
        console.log('Google Sign-In Successful. User details:', this.user);
      } else {
        throw new Error('No user returned from sign-in');
      }
    } catch (error: any) {
      // More detailed error handling
      console.error('Google Sign-In Error:', error);

      // Common Firebase Authentication Errors
      const errorCode = error.code;
      switch (errorCode) {
        case 'auth/account-exists-with-different-credential':
          this.message = 'An account already exists with a different credential.';
          break;
        case 'auth/popup-blocked':
          this.message = 'Authentication popup was blocked. Please enable popups.';
          break;
        case 'auth/popup-closed-by-user':
          this.message = 'Authentication popup was closed before completion.';
          break;
        case 'auth/internal-error':
          this.message = 'An internal error occurred. Please check your Firebase configuration.';
          break;
        default:
          this.message = `Sign-In Failed: ${error.message}`;
      }
    }
  }

  async signOut() {
    try {
      await this.auth.signOut();
      this.message = 'You have signed out successfully.';
      this.user = null;
      console.log('User signed out.');
    } catch (error: any) {
      this.message = `Sign-Out Failed: ${error.message}`;
      console.error('Sign-Out Error:', error);
    }
  }
}
