import { Component, OnInit, inject } from '@angular/core';
import { getAuth, onAuthStateChanged, signOut, createUserWithEmailAndPassword, signInWithEmailAndPassword, connectAuthEmulator } from 'firebase/auth';
import { initializeApp } from 'firebase/app';
import { environment } from '../../enviroment';

@Component({
  selector: 'app-authentication',
  standalone: true,
  templateUrl: './authentication.component.html',
  styleUrls: ['./authentication.component.scss'],
})
export class AuthenticationComponent implements OnInit {
  private auth = inject(getAuth(initializeApp(environment.firebaseConfig))); // Dependency Injection for Auth
  email: string = '';
  password: string = '';
  message: string = '';
  user: any = null;

  ngOnInit() {
    this.monitorAuthState();
    connectAuthEmulator(this.auth, 'http://localhost:9099');
  }

  // Login with email and password
  async loginEmailPassword() {
    try {
      const userCredential = await signInWithEmailAndPassword(this.auth, this.email, this.password);
      console.log('Logged in:', userCredential.user);
      this.message = `Welcome, ${userCredential.user.email}!`;
    } catch (error: any) {
      console.error('Login Error:', error.message);
      this.message = `Login Error: ${error.message}`;
    }
  }

  // Create new account with email and password
  async createAccount() {
    try {
      const userCredential = await createUserWithEmailAndPassword(this.auth, this.email, this.password);
      console.log('Account created:', userCredential.user);
      this.message = `Account created for ${userCredential.user.email}!`;
    } catch (error: any) {
      console.error('Sign-Up Error:', error.message);
      this.message = `Sign-Up Error: ${error.message}`;
    }
  }

  // Monitor authentication state
  monitorAuthState() {
    onAuthStateChanged(this.auth, (user) => {
      if (user) {
        this.user = user;
        this.message = `Welcome, ${user.email || 'User'}!`;
      } else {
        this.user = null;
        this.message = 'You are not logged in.';
      }
    });
  }

  // Log out
  async logout() {
    try {
      await signOut(this.auth);
      console.log('User signed out');
      this.user = null;
      this.message = 'Signed out successfully.';
    } catch (error: any) {
      console.error('Sign-Out Error:', error.message);
      this.message = `Sign-Out Error: ${error.message}`;
    }
  }
}
