import { Component } from '@angular/core';
import { initializeApp } from 'firebase/app';
import { getAuth, GoogleAuthProvider, signInWithPopup, Auth } from 'firebase/auth';
import { CommonModule } from '@angular/common';
import { environment } from '../../enviroment'; // Ensure this path is correct

// Initialize Firebase
const firebaseApp = initializeApp(environment.firebaseConfig);

@Component({
  selector: 'app-authentication',
  standalone: true, // Mark the component as standalone
  imports: [CommonModule], // Import required modules directly
  templateUrl: './authentication.component.html',
  styleUrls: ['./authentication.component.scss'],
})
export class AuthenticationComponent {
  private auth: Auth = getAuth(firebaseApp); // Firebase Auth instance
  message: string = ''; // Feedback message for the user
  user: any = null; // User details after Google sign-in
  items: string[] = ['Item 1', 'Item 2', 'Item 3']; // Example data for @for loop

  /**
   * Sign in using Google account via Firebase Authentication
   */
  async googleSignIn() {
    try {
      const provider = new GoogleAuthProvider(); // Google provider configuration
      const result = await signInWithPopup(this.auth, provider);
      this.user = result.user;

      // Display success message
      this.message = `Welcome, ${this.user.displayName || 'User'}! You are signed in with Google.`;
      console.log('Google Sign-In Successful. User details:', this.user);
    } catch (error: any) {
      // Handle sign-in errors
      this.message = `Sign-In Failed: ${error.message}`;
      console.error('Google Sign-In Error:', error);
    }
  }

  /**
   * Sign out the currently logged-in user
   */
  async signOut() {
    try {
      await this.auth.signOut();
      this.message = 'You have signed out successfully.';
      this.user = null;
      console.log('User signed out.');
    } catch (error: any) {
      // Handle sign-out errors
      this.message = `Sign-Out Failed: ${error.message}`;
      console.error('Sign-Out Error:', error);
    }
  }
}
